import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import GeneralInfoSidebar from './GeneralInfoSidebar';
import AnimalSidebar from './AnimalSidebar';
import SearchSidebar from './SearchSidebar';
import HelpSidebar from './HelpSidebar';
import AnimalList from './AnimalList';
import './HomePage.css';

const HomePage = ({ currentUser }) => {
  return (
    <div>
      <header className="bg-warning text-center py-4">
        <h1>Salvando Vidas Desde 1997</h1>
        {currentUser ? (
          <div>
            <p>Bienvenido, {currentUser.username}</p>
            <Button variant="secondary" onClick={() => window.location.reload()}>Cerrar Sesión</Button>
          </div>
        ) : (
          <p>
            <Link to="/login">Iniciar Sesión</Link> | <Link to="/register">Registrarse</Link>
          </p>
        )}
      </header>
      <Container fluid>
        <Row>
          <Col md={3}>
            <GeneralInfoSidebar />
            <AnimalSidebar />
            <SearchSidebar />
          </Col>
          <Col md={6}>
            <AnimalList />
          </Col>
          <Col md={3}>
            <HelpSidebar />
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default HomePage;
