import React, { useState } from 'react';
import AnimalList from './components/AnimalList';
import Sidebar from './components/Sidebar';
import AnimalForm from './components/AnimalForm';
import './App.css';

const App = () => {
  const [showForm, setShowForm] = useState(false);
  const [animalData, setAnimalData] = useState(null);

  const handleFormSubmit = (data) => {
    console.log('Animal Data:', data);
    // Aquí puedes agregar la lógica para guardar los datos en el backend
    setAnimalData(data);
    setShowForm(false);
  };

  const handleAddAnimalClick = () => {
    setShowForm(true);
  };

  return (
    <div>
      <header className="bg-warning text-center py-4">
        <h1>Salvando Vidas Desde</h1>
      </header>
      <div className="container">
        <div className="row">
          <Sidebar />
          <div className="col-md-9">
            {showForm ? (
              <AnimalForm onSubmit={handleFormSubmit} />
            ) : (
              <div>
                <button className="btn btn-primary mb-3" onClick={handleAddAnimalClick}>
                  Añadir Animal
                </button>
                <AnimalList />
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
