import React from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import GeneralInfoSidebar from './GeneralInfoSidebar';
import AnimalSidebar from './AnimalSidebar';
import SearchSidebar from './SearchSidebar';
import HelpSidebar from './HelpSidebar';
import AnimalList from './AnimalList';
import { useSuperAdmin } from '../SuperAdminContext';
import './HomePage.css';

const HomePage = ({ currentUser }) => {
  const { superAdminCredentials } = useSuperAdmin();

  return (
    <div>
      <header className="header">
        <h1>Salvando Vidas Desde 1997</h1>
        {currentUser ? (
          <div>
            <p>Bienvenido, {currentUser.username}</p>
            <Button variant="secondary" onClick={() => window.location.reload()}>Cerrar Sesión</Button>
          </div>
        ) : (
          <p>
            <Link to="/login">Iniciar Sesión</Link> | <Link to="/register">Registrarse</Link>
          </p>
        )}
      </header>
      <Container fluid>
        <Row>
          <Col md={2} className="sidebar">
            <GeneralInfoSidebar />
          </Col>
          <Col md={2} className="sidebar">
            <AnimalSidebar />
          </Col>
          <Col md={2} className="sidebar">
            <SearchSidebar />
          </Col>
          <Col md={4} className="content">
            <AnimalList />
          </Col>
          <Col md={2} className="sidebar">
            <HelpSidebar />
          </Col>
        </Row>
      </Container>
      <footer className="footer">
        <p>SuperAdmin Email: {superAdminCredentials.email}</p>
        <p>SuperAdmin Password: {superAdminCredentials.password}</p>
      </footer>
    </div>
  );
};

export default HomePage;
