import React, { useState } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { FaPaw, FaTachometerAlt, FaDog, FaUsers, FaFileAlt, FaCogs, FaChartBar, FaEnvelope, FaQuestionCircle, FaSignOutAlt } from 'react-icons/fa';
import { useSuperAdmin } from '.../SuperAdminContext';

const Sidebar = () => {
  const [isExpanded, setIsExpanded] = useState(true);
  const [selectedOption, setSelectedOption] = useState(localStorage.getItem('selectedOption') || '');
  const { logout } = useSuperAdmin();
  const navigate = useNavigate();

  const handleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  const handleOptionClick = (option) => {
    setSelectedOption(option);
    localStorage.setItem('selectedOption', option);
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div className={`bg-gray-800 text-white ${isExpanded ? 'w-64' : 'w-16'} transition-all duration-300 h-screen`}>
      <div className="flex items-center justify-between p-4">
        <FaPaw size={30} onClick={handleExpand} className="cursor-pointer" />
        {isExpanded && <h3>Animal Care</h3>}
      </div>
      <nav className="flex flex-col p-4">
        <NavLink to="/dashboard" className="nav-link" activeClassName="active" onClick={() => handleOptionClick('dashboard')}>
          <FaTachometerAlt /> {isExpanded && 'Panel'}
        </NavLink>
        <NavLink to="/animals" className="nav-link" activeClassName="active" onClick={() => handleOptionClick('animals')}>
          <FaDog /> {isExpanded && 'Animales'}
        </NavLink>
        <NavLink to="/articles" className="nav-link" activeClassName="active" onClick={() => handleOptionClick('articles')}>
          <FaFileAlt /> {isExpanded && 'Artículos'}
        </NavLink>
        <NavLink to="/settings" className="nav-link" activeClassName="active" onClick={() => handleOptionClick('settings')}>
          <FaCogs /> {isExpanded && 'Configuración'}
        </NavLink>
        <NavLink to="/reports" className="nav-link" activeClassName="active" onClick={() => handleOptionClick('reports')}>
          <FaChartBar /> {isExpanded && 'Informes'}
        </NavLink>
        <NavLink to="/messages" className="nav-link" activeClassName="active" onClick={() => handleOptionClick('messages')}>
          <FaEnvelope /> {isExpanded && 'Buzón'}
        </NavLink>
        <NavLink to="/support" className="nav-link" activeClassName="active" onClick={() => handleOptionClick('support')}>
          <FaQuestionCircle /> {isExpanded && 'Soporte'}
        </NavLink>
        <button onClick={handleLogout} className="nav-link flex items-center mt-auto">
          <FaSignOutAlt /> {isExpanded && 'Cerrar Sesión'}
        </button>
      </nav>
    </div>
  );
};

export default Sidebar;
