import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Sidebar from './components/Dashboard/Sidebar';
import Dashboard from './components/Dashboard/Dashboard';
import Animals from './components/Dashboard/Animals';
import Register from './components/Register';
import Login from './components/Login';
import './App.css';

const App = () => {
  const [users, setUsers] = useState([
    { 
      username: 'superadmin', 
      email: 'superadmin@example.com', 
      password: 'superadmin123', 
      role: 'Superadministrador' 
    }
  ]);
  const [currentUser, setCurrentUser] = useState(null);

  const handleRegister = (newUser) => {
    setUsers([...users, newUser]);
  };

  const handleLogin = (user) => {
    setCurrentUser(user);
  };

  return (
    <Router>
      <div className="d-flex">
        {currentUser && <Sidebar />}
        <div className={currentUser ? "content" : "content-full"}>
          <Routes>
            <Route path="/register" element={<Register onRegister={handleRegister} />} />
            <Route path="/login" element={<Login users={users} onLogin={handleLogin} />} />
            {currentUser && <Route path="/dashboard" element={<Dashboard />} />}
            {currentUser && <Route path="/animals" element={<Animals />} />}
            {/* Agrega más rutas según sea necesario */}
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
