import React from 'react';
import { Link } from 'react-router-dom';
import { Container, Row, Col, Button } from 'react-bootstrap';
import GeneralInfoSidebar from './GeneralInfoSidebar';
import AnimalSidebar from './AnimalSidebar';
import SearchSidebar from './SearchSidebar';
import HelpSidebar from './HelpSidebar';
import AnimalList from './AnimalList';
import './HomePage.css';

const HomePage = ({ currentUser }) => {
  return (
    <div>
      <header className="bg-warning text-center py-4">
        <h1>Salvando Vidas Desde 1997</h1>
        
          <p>
            <Link to="/login">Iniciar Sesi√≥n</Link> | <Link to="/register">Registrarse</Link>
          </p>
        
      </header>
      <Container fluid>
      <Row>
        {/* Barra lateral izquierda */}
        <Col md={2}>
          <GeneralInfoSidebar />
          <AnimalSidebar />
          <SearchSidebar />
        </Col>

        {/* Contenido principal */}
        <Col md={6}>
          <h1>Listado de animales</h1>
          <AnimalList />
        </Col>

        {/* Barra lateral derecha */}
        <Col md={2}>
          <HelpSidebar />
        </Col>
      </Row>
    </Container>
    </div>
  );
};

export default HomePage;
