import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
import UserForm from './components/UserForm';
import UserList from './components/UserList';
import AnimalForm from './components/AnimalForm';
import AnimalList from './components/AnimalList';
import { ROLES } from './data/roles';
import './App.css';

const App = () => {
  const [showUserForm, setShowUserForm] = useState(false);
  const [showAnimalForm, setShowAnimalForm] = useState(false);
  const [users, setUsers] = useState([
    { username: 'superadmin', email: 'superadmin@example.com', role: ROLES.SUPER_ADMIN, userType: 'N/A' }
  ]);
  const [animals, setAnimals] = useState([]);

  const currentUser = users[0]; // Asumimos que el primer usuario es el que está actualmente logueado

  const handleUserFormSubmit = (user) => {
    setUsers([...users, user]);
    setShowUserForm(false);
  };

  const handleAnimalFormSubmit = (animal) => {
    setAnimals([...animals, animal]);
    setShowAnimalForm(false);
  };

  const handleAddUserClick = () => {
    setShowUserForm(true);
    setShowAnimalForm(false);
  };

  const handleAddAnimalClick = () => {
    setShowAnimalForm(true);
    setShowUserForm(false);
  };

  const filteredUsers = currentUser.role === ROLES.SUPER_ADMIN
    ? users
    : users.filter(user => user.role !== ROLES.SUPER_ADMIN);

  return (
    <div>
      <header className="bg-warning text-center py-4">
        <h1>Gestión de Usuarios y Animales</h1>
      </header>
      <div className="container">
        {currentUser.role === ROLES.SUPER_ADMIN && (
          <Button variant="primary" onClick={handleAddUserClick} className="mr-2">
            {showUserForm ? 'Cancelar' : 'Agregar Usuario'}
          </Button>
        )}
        <Button variant="primary" onClick={handleAddAnimalClick}>
          {showAnimalForm ? 'Cancelar' : 'Agregar Animal'}
        </Button>
        {showUserForm && <UserForm onSubmit={handleUserFormSubmit} currentUser={currentUser} />}
        {showAnimalForm && <AnimalForm onSubmit={handleAnimalFormSubmit} />}
        <UserList users={filteredUsers} />
        <AnimalList animals={animals} />
      </div>
    </div>
  );
};

export default App;
